.footer {
  background: linear-gradient(135deg, #2d3748 0%, #1a202c 100%);
  color: white;
  padding: 4rem 0 0;
  position: relative;
}

.footer::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 25% 25%, rgba(102, 126, 234, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 75% 75%, rgba(139, 92, 246, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.footer-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
}

.footer-content {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1.5fr;
  gap: 3rem;
  padding-bottom: 3rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-section h4 {
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
  color: #ffd700;
  position: relative;
}

.footer-section h4::after {
  content: '';
  display: block;
  width: 30px;
  height: 2px;
  background: #ffd700;
  margin-top: 0.5rem;
}

.footer-logo h3 {
  font-size: 1.8rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: white;
}

.logo-subtitle {
  color: #a0aec0;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 1rem;
}

.company-description {
  color: #cbd5e0;
  line-height: 1.6;
  margin-bottom: 1.5rem;
  font-size: 0.95rem;
}

.company-certifications {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.cert-badge {
  background: rgba(255, 215, 0, 0.1);
  color: #ffd700;
  padding: 0.3rem 0.8rem;
  border-radius: 15px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid rgba(255, 215, 0, 0.3);
}

.quick-links ul,
.services-links ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.quick-links li,
.services-links li {
  margin-bottom: 0.8rem;
}

.quick-links button {
  background: none;
  border: none;
  color: #cbd5e0;
  font-size: 0.95rem;
  cursor: pointer;
  transition: color 0.3s ease;
  padding: 0;
  text-align: left;
}

.quick-links button:hover {
  color: #ffd700;
}

.services-links span {
  color: #cbd5e0;
  font-size: 0.95rem;
  display: block;
  transition: color 0.3s ease;
}

.services-links span:hover {
  color: #ffd700;
  cursor: default;
}

.contact-item {
  display: flex;
  align-items: center;
  gap: 0.8rem;
  margin-bottom: 1rem;
  color: #cbd5e0;
  font-size: 0.95rem;
}

.contact-icon {
  font-size: 1.2rem;
  width: 25px;
  text-align: center;
}

.social-media {
  margin-top: 1.5rem;
}

.social-media h5 {
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #ffd700;
}

.social-icons {
  display: flex;
  gap: 0.8rem;
}

.social-icon {
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
}

.social-icon:hover {
  background: rgba(255, 215, 0, 0.2);
  border-color: #ffd700;
  transform: translateY(-2px);
}

.footer-bottom {
  padding: 2rem 0;
  background: rgba(0, 0, 0, 0.2);
  margin: 0 -2rem;
  margin-top: 3rem;
}

.footer-bottom-content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 1rem;
}

.copyright p {
  color: #a0aec0;
  font-size: 0.9rem;
  margin: 0;
  line-height: 1.4;
}

.footer-links {
  display: flex;
  gap: 2rem;
  align-items: center;
}

.footer-link {
  background: none;
  border: none;
  color: #a0aec0;
  font-size: 0.9rem;
  cursor: pointer;
  transition: color 0.3s ease;
  padding: 0;
}

.footer-link:hover {
  color: #ffd700;
}

.scroll-to-top {
  background: linear-gradient(45deg, #667eea, #764ba2);
  border: none;
  border-radius: 8px;
  padding: 0.8rem 1rem;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.2rem;
  font-size: 0.8rem;
  font-weight: 600;
  min-width: 60px;
}

.scroll-to-top:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 20px rgba(102, 126, 234, 0.4);
}

.scroll-to-top span:first-child {
  font-size: 1.2rem;
  font-weight: bold;
}

/* Mobile responsive */
@media (max-width: 768px) {
  .footer {
    padding: 3rem 0 0;
  }

  .footer-container {
    padding: 0 1rem;
  }

  .footer-content {
    grid-template-columns: 1fr;
    gap: 2.5rem;
    text-align: center;
  }

  .footer-section h4::after {
    margin: 0.5rem auto 0;
  }

  .company-certifications {
    justify-content: center;
  }

  .quick-links button {
    text-align: center;
  }

  .contact-item {
    justify-content: center;
  }

  .social-icons {
    justify-content: center;
  }

  .footer-bottom-content {
    flex-direction: column;
    text-align: center;
    gap: 1.5rem;
  }

  .footer-links {
    justify-content: center;
  }
}

@media (max-width: 480px) {
  .footer-content {
    gap: 2rem;
  }

  .footer-links {
    flex-direction: column;
    gap: 1rem;
  }

  .company-certifications {
    flex-direction: column;
    align-items: center;
  }

  .cert-badge {
    text-align: center;
  }
}
