 /* 계단 블록 */
 .stair-block {

commits.content:
.infinite-stairs-container {
  width: 100vw;
  height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  perspective: 1000px;
  overflow: hidden;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}

.stairs-wrapper {
  position: absolute;
  transform-style: preserve-3d;
  animation: infiniteStairs 20s linear infinite;
}

.stairs-secondary {
  animation-delay: -10s;
}

@keyframes infiniteStairs {
  0% {
    transform: translateY(100vh) rotateX(15deg) rotateY(-15deg);
  }
  100% {
    transform: translateY(-100vh) rotateX(15deg) rotateY(-15deg);
  }
}

.stair {
  position: absolute;
  transform-style: preserve-3d;
  animation: stairCurve 4s ease-in-out infinite;
}

@keyframes stairCurve {
  0%, 100% {
    transform: translateX(0) rotateZ(0deg);
  }
  25% {
    transform: translateX(50px) rotateZ(5deg);
  }
  50% {
    transform: translateX(0) rotateZ(0deg);
  }
  75% {
    transform: translateX(-50px) rotateZ(-5deg);
  }
}

.stair-step {
  position: relative;
  width: 100px;
  height: 20px;
  transform-style: preserve-3d;
  margin-bottom: 30px;
}

.stair-top {
  position: absolute;
  width: 100px;
  height: 40px;
  background: linear-gradient(45deg, #ff6b6b, #ee5a52);
  transform: rotateX(90deg) translateZ(10px);
  border-radius: 5px;
  box-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
}

.stair-front {
  position: absolute;
  width: 100px;
  height: 20px;
  background: linear-gradient(180deg, #ee5a52, #c44569);
  transform: translateZ(20px);
  border-radius: 0 0 5px 5px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
}

.stair-side {
  position: absolute;
  width: 40px;
  height: 20px;
  background: linear-gradient(90deg, #c44569, #a93d5e);
  transform: rotateY(90deg) translateZ(20px) translateX(40px);
  border-radius: 0 5px 5px 0;
}

/* 반응형 디자인 */
@media (max-width: 768px) {
  .stair-step {
    width: 80px;
    height: 16px;
  }

  .stair-top {
    width: 80px;
    height: 32px;
  }

  .stair-front {
    width: 80px;
    height: 16px;
  }

  .stair-side {
    width: 32px;
    height: 16px;
  }

  @keyframes stairCurve {
    0%, 100% {
      transform: translateX(0) rotateZ(0deg);
    }
    25% {
      transform: translateX(30px) rotateZ(3deg);
    }
    50% {
      transform: translateX(0) rotateZ(0deg);
    }
    75% {
      transform: translateX(-30px) rotateZ(-3deg);
    }
  }
}

/* 호버 효과 */
.stair:hover .stair-step {
  animation-play-state: paused;
}

.stair:hover .stair-top {
  box-shadow: 0 0 30px rgba(255, 107, 107, 0.8);
  transform: rotateX(90deg) translateZ(15px) scale(1.1);
}

/* 추가 시각적 효과 */
.infinite-stairs-container::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(circle at center, transparent 0%, rgba(0, 0, 0, 0.1) 100%);
  pointer-events: none;
}

/* 계단 색상 변화 */
.stair:nth-child(even) .stair-top {
  background: linear-gradient(45deg, #4ecdc4, #44a08d);
  box-shadow: 0 0 20px rgba(78, 205, 196, 0.5);
}

.stair:nth-child(even) .stair-front {
  background: linear-gradient(180deg, #44a08d, #093637);
}

.stair:nth-child(even) .stair-side {
  background: linear-gradient(90deg, #093637, #065a60);
}

.stair:nth-child(3n) .stair-top {
  background: linear-gradient(45deg, #ffecd2, #fcb69f);
  box-shadow: 0 0 20px rgba(252, 182, 159, 0.5);
}

.stair:nth-child(3n) .stair-front {
  background: linear-gradient(180deg, #fcb69f, #ff9a9e);
}

.stair:nth-child(3n) .stair-side {
  background: linear-gradient(90deg, #ff9a9e, #fad0c4);
}
