commits.content:
.order-summary {
  height: 100%;
  display: flex;
  flex-direction: column;
}

.order-summary h2 {
  margin: 0 0 1.5rem 0;
  font-size: 1.5rem;
  color: #2d3748;
  text-align: center;
  padding-bottom: 1rem;
  border-bottom: 2px solid #f7fafc;
}

.empty-order {
  text-align: center;
  padding: 3rem 1rem;
  color: #a0aec0;
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.empty-order p {
  margin: 0 0 1rem 0;
  font-size: 1.1rem;
}

.empty-icon {
  font-size: 3rem;
  opacity: 0.5;
}

.order-items {
  flex: 1;
  max-height: 400px;
  overflow-y: auto;
  margin-bottom: 1.5rem;
}

.order-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  margin-bottom: 0.5rem;
  background: #f7fafc;
  border-radius: 10px;
  transition: all 0.2s ease;
}

.order-item:hover {
  background: #edf2f7;
}

.item-info {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
}

.item-name {
  font-weight: 600;
  color: #2d3748;
  font-size: 0.95rem;
}

.item-quantity {
  font-size: 0.85rem;
  color: #718096;
}

.item-controls {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.item-price {
  font-weight: 600;
  color: #667eea;
  font-size: 0.9rem;
  min-width: 70px;
  text-align: right;
}

.remove-btn {
  width: 28px;
  height: 28px;
  border: none;
  background: #fed7d7;
  color: #e53e3e;
  border-radius: 50%;
  cursor: pointer;
  font-size: 1.2rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
}

.remove-btn:hover {
  background: #feb2b2;
  transform: scale(1.1);
}

.order-total {
  border-top: 2px solid #f7fafc;
  padding-top: 1rem;
  margin-bottom: 1.5rem;
}

.total-line {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 1.2rem;
  font-weight: 700;
  color: #2d3748;
}

.total-price {
  color: #667eea;
  font-size: 1.4rem;
}

.order-actions {
  display: flex;
  gap: 0.75rem;
  flex-direction: column;
}

.clear-btn,
.payment-btn {
  padding: 0.875rem 1.5rem;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  font-weight: 600;
  font-size: 1rem;
  transition: all 0.3s ease;
}

.clear-btn {
  background: #fed7d7;
  color: #e53e3e;
}

.clear-btn:hover {
  background: #feb2b2;
  transform: translateY(-2px);
}

.payment-btn {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
}

.payment-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(102, 126, 234, 0.4);
}

/* 스크롤바 스타일링 */
.order-items::-webkit-scrollbar {
  width: 6px;
}

.order-items::-webkit-scrollbar-track {
  background: #f1f1f1;
  border-radius: 10px;
}

.order-items::-webkit-scrollbar-thumb {
  background: #cbd5e0;
  border-radius: 10px;
}

.order-items::-webkit-scrollbar-thumb:hover {
  background: #a0aec0;
}

@media (max-width: 768px) {
  .order-summary h2 {
    font-size: 1.3rem;
  }

  .order-items {
    max-height: 300px;
  }

  .order-actions {
    flex-direction: row;
  }

  .clear-btn,
  .payment-btn {
    flex: 1;
    padding: 0.75rem 1rem;
    font-size: 0.9rem;
  }
}
