.services {
  padding: 6rem 0;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  position: relative;
  overflow: hidden;
}

.services::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 20% 80%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255, 255, 255, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.services-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
}

.section-header {
  text-align: center;
  margin-bottom: 4rem;
}

.section-title {
  font-size: 3rem;
  font-weight: 700;
  color: white;
  margin-bottom: 1rem;
  position: relative;
}

.section-title::after {
  content: '';
  display: block;
  width: 80px;
  height: 4px;
  background: linear-gradient(45deg, #ffd700, #ffed4e);
  margin: 1rem auto;
  border-radius: 2px;
}

.section-subtitle {
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.9);
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
}

.services-content {
  background: white;
  border-radius: 20px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  margin-bottom: 4rem;
}

.services-tabs {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  background: #f8f9fa;
  border-bottom: 1px solid #e9ecef;
}

.service-tab {
  padding: 1.5rem 1rem;
  border: none;
  background: transparent;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  text-align: center;
  border-bottom: 3px solid transparent;
}

.service-tab:hover {
  background: rgba(102, 126, 234, 0.1);
}

.service-tab.active {
  background: rgba(102, 126, 234, 0.1);
  border-bottom-color: #667eea;
}

.tab-icon {
  font-size: 2rem;
  display: block;
}

.tab-title {
  font-size: 0.9rem;
  font-weight: 600;
  color: #2d3748;
  line-height: 1.3;
}

.service-details {
  padding: 3rem;
}

.service-header {
  display: flex;
  align-items: center;
  gap: 2rem;
  margin-bottom: 3rem;
  padding-bottom: 2rem;
  border-bottom: 1px solid #e9ecef;
}

.service-icon {
  font-size: 4rem;
  background: linear-gradient(45deg, #667eea, #764ba2);
  width: 100px;
  height: 100px;
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

.service-info h3 {
  font-size: 2rem;
  font-weight: 700;
  color: #2d3748;
  margin-bottom: 0.5rem;
}

.service-info p {
  font-size: 1.1rem;
  color: #718096;
  line-height: 1.6;
}

.service-content-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 3rem;
  margin-bottom: 3rem;
}

.features-section h4,
.benefits-section h4 {
  font-size: 1.3rem;
  font-weight: 600;
  color: #2d3748;
  margin-bottom: 1.5rem;
  position: relative;
}

.features-section h4::before {
  content: '⚡';
  margin-right: 0.5rem;
}

.benefits-section h4::before {
  content: '✨';
  margin-right: 0.5rem;
}

.features-list,
.benefits-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.features-list li,
.benefits-list li {
  padding: 0.8rem 0;
  color: #4a5568;
  position: relative;
  padding-left: 2rem;
  border-bottom: 1px solid #f7fafc;
  transition: all 0.3s ease;
}

.features-list li:hover,
.benefits-list li:hover {
  background: #f7fafc;
  padding-left: 2.5rem;
}

.features-list li::before {
  content: '▶';
  position: absolute;
  left: 0;
  color: #667eea;
  font-size: 0.8rem;
}

.benefits-list li::before {
  content: '✓';
  position: absolute;
  left: 0;
  color: #48bb78;
  font-weight: bold;
}

.service-cta {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
}

.cta-button {
  padding: 1rem 2rem;
  border: none;
  border-radius: 50px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  min-width: 160px;
}

.cta-button:not(.secondary) {
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

.cta-button:not(.secondary):hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.6);
}

.cta-button.secondary {
  background: transparent;
  color: #667eea;
  border: 2px solid #667eea;
}

.cta-button.secondary:hover {
  background: #667eea;
  color: white;
  transform: translateY(-3px);
}

.additional-services {
  text-align: center;
}

.additional-services h3 {
  font-size: 2rem;
  font-weight: 700;
  color: white;
  margin-bottom: 2.5rem;
}

.additional-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 2rem;
}

.additional-item {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 15px;
  padding: 2rem;
  text-align: center;
  transition: all 0.3s ease;
}

.additional-item:hover {
  transform: translateY(-5px);
  background: rgba(255, 255, 255, 0.15);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.additional-icon {
  font-size: 2.5rem;
  margin-bottom: 1rem;
  display: block;
}

.additional-item h4 {
  font-size: 1.2rem;
  font-weight: 600;
  color: white;
  margin-bottom: 0.8rem;
}

.additional-item p {
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.5;
  font-size: 0.95rem;
}

/* Mobile responsive */
@media (max-width: 768px) {
  .services {
    padding: 4rem 0;
  }

  .services-container {
    padding: 0 1rem;
  }

  .section-title {
    font-size: 2.5rem;
  }

  .section-subtitle {
    font-size: 1.1rem;
  }

  .services-tabs {
    grid-template-columns: repeat(2, 1fr);
  }

  .service-tab {
    padding: 1rem 0.5rem;
  }

  .tab-title {
    font-size: 0.8rem;
  }

  .service-details {
    padding: 2rem;
  }

  .service-header {
    flex-direction: column;
    text-align: center;
    gap: 1.5rem;
  }

  .service-icon {
    width: 80px;
    height: 80px;
    font-size: 3rem;
  }

  .service-info h3 {
    font-size: 1.6rem;
  }

  .service-content-grid {
    grid-template-columns: 1fr;
    gap: 2rem;
  }

  .service-cta {
    flex-direction: column;
    align-items: center;
  }

  .cta-button {
    width: 100%;
    max-width: 250px;
  }

  .additional-grid {
    grid-template-columns: repeat(2, 1fr);
    gap: 1.5rem;
  }

  .additional-item {
    padding: 1.5rem;
  }
}

@media (max-width: 480px) {
  .section-title {
    font-size: 2rem;
  }

  .services-tabs {
    grid-template-columns: 1fr;
  }

  .service-details {
    padding: 1.5rem;
  }

  .service-info h3 {
    font-size: 1.4rem;
  }

  .additional-services h3 {
    font-size: 1.6rem;
  }

  .additional-grid {
    grid-template-columns: 1fr;
  }
}
